gif-animation.content:
// import
import { Illustrator } from "../../dist/index.mjs";
// use this for prod
// import { Illustrator, Tools } from "illustrator.js";
import fs from "fs";

// create illustrator instance
const illustrator = new Illustrator(512, 512);

// colors array
const colors = ["#FFFFFF", "#FF0000", "#FFFF00", "#FF00FF", "#00FF00", "#0000FF"];

// for a color in colors array, create new layer and fill the layer with that color
for (const color of colors) {
    // create new layer
    const layer = illustrator.layers.createLayer({
        name: color
    });
    // create background color tool
    const bgTool = new layer.tools.get("BackgroundColorTool");
    // set fill color
    bgTool.setFillColor(color);
    // fill background
    bgTool.fill(0, 0, illustrator.width, illustrator.height);
    // render this tool to the layer
    bgTool.render();
}

// get animation api
const animation = illustrator.animation;
// get all layers and transform to animation frame
const layers = illustrator.layers.getAllLayers().map(m => ({
    duration: 500,
    frame: m.layer
}));
// animation config, set repeat to infinite (or 0) and add our layers to frames
animation.setRepeat(0).addFrames(layers);

// render the frames
const output = await animation.createAnimation();
// write the output file
output.pipe(fs.createWriteStream("./animation.gif"));

triggered-animation.content:
// import
import { Illustrator } from "../../dist/index.mjs";
// use this for prod
// import { Illustrator, Tools } from "illustrator.js";
import fs from "fs";

const OVERLAY_COLOR = "#FF000033";
const illustrator = new Illustrator(256, 310);

let img, base;

illustrator.backgroundLayer.hide();
illustrator.animation.setFrameDelay(15).setRepeat(0);

for (let i = 0; i < 9; i++) {
    const layer = illustrator.layers.createLayer({ name: `Layer ${i}` });
    const imgTool = layer.tools.get("ImageTool");
    const bgTool = layer.tools.get("BackgroundColorTool");
    if (!img) img = await imgTool.load("https://cdn.discordapp.com/embed/avatars/0.png?size=4096");
    if (!base) base = await imgTool.load("./triggered-base.png");

    imgTool.draw(
        img,
        Math.floor(Math.random() * 30) - 30,
        Math.floor(Math.random() * 30) - 30,
        256 + 30,
        310 - 54 + 30
    );

    imgTool.draw(
        base,
        Math.floor(Math.random() * 20) - 20,
        310 - 54 + Math.floor(Math.random() * 20) - 20,
        256 + 20,
        54 + 20
    );

    imgTool.render();

    bgTool.setFillColor(OVERLAY_COLOR);
    bgTool.fill(0, 0, 256, 310);
    bgTool.render();
}

const layers = illustrator.layers.getAllLayers().map(m => ({
    frame: m.layer
}));

illustrator.animation.addFrames(layers);

const output = await illustrator.animation.createAnimation();
output.pipe(fs.createWriteStream("./triggered.gif"));

